const items = $input.all();
const userMatches = {};

for (const item of items) {
  const userId = item.json.user_id;
  if (!userMatches[userId]) {
    userMatches[userId] = {
      user_id: userId,
      user_name: item.json.user_name,
      user_email: item.json.user_email,
      matches: []
    };
  }
  userMatches[userId].matches.push({
    provider: item.json.provider_name,
    product: item.json.product_name,
    rate_min: item.json.interest_rate_min,
    rate_max: item.json.interest_rate_max,
    amount_min: item.json.loan_amount_min,
    amount_max: item.json.loan_amount_max,
    score: item.json.match_score
  });
}

return Object.values(userMatches).map(u => ({ json: u }));

const user = $input.item.json;
const matches = user.matches.sort((a, b) => b.score - a.score).slice(0, 5);

let html = `<h2>Hello ${user.user_name}!</h2>
<p>Great news! Based on your profile, we found ${matches.length} loan products you may qualify for:</p>
<table border="1" cellpadding="10" style="border-collapse: collapse;">
<tr><th>Provider</th><th>Product</th><th>Interest Rate</th><th>Loan Amount</th><th>Match Score</th></tr>`;

for (const m of matches) {
  html += `<tr>
    <td>${m.provider}</td>
    <td>${m.product}</td>
    <td>${m.rate_min}% - ${m.rate_max}%</td>
    <td>â‚¹${m.amount_min.toLocaleString()} - â‚¹${m.amount_max.toLocaleString()}</td>
    <td>${m.score}%</td>
  </tr>`;
}

html += `</table>
<p>Best regards,<br>Loan Eligibility Engine</p>`;

return {
  json: {
    to: user.user_email,
    subject: `ðŸŽ‰ ${matches.length} Personal Loan Offers for You!`,
    html: html,
    user_id: user.user_id
  }
};
